  { category: "Radio Navigation", test: 3, id: 1007, question: "Refer to Appendix A, diagram E. What is the track from BANTU to ZAPPO?", options: ["360 (M)", "130 (M)", "360 (T)", "130 (T)"], answer: "360 (M)" },
  { category: "Radio Navigation", test: 3, id: 1008, question: "The principle error in GNSS is:", options: ["ionospheric propagation", "GDOP", "receiver clock error", "SV ephemeris error"], answer: "ionospheric propagation" },
  { category: "Radio Navigation", test: 3, id: 1009, question: "The number of SVs required to produce a 3D fix is:", options: ["3", "4", "5", "6"], answer: "4" },
  { category: "Radio Navigation", test: 3, id: 1010, question: "Refer to Appendix A. Which diagram shows the MAP display?", options: ["Diagram A", "Diagram B", "Diagram C", "Diagram D", "Diagram E", "Diagram F"], answer: "Diagram A" },
  { category: "Radio Navigation", test: 3, id: 1011, question: "The DME in an aircraft at FL630 measures a slant range of 16 NM from a ground station at 1225 ft AMSL. The plan range is:", options: ["12.5 NM", "19 NM", "16 NM", "10.5 NM"], answer: "12.5 NM" },
  { category: "Radio Navigation", test: 3, id: 1012, question: "The altitude read-out at the ground station from a mode C response will give the aircraft altitude within:", options: ["300 ft", "100 ft", "500 ft", "50 ft"], answer: "100 ft" },
  { category: "Radio Navigation", test: 4, id: 1013, question: "Refer to Appendix A, diagram C. What is the symbol designated DFC which is coloured cyan? [Image E is missing, based on checkmark]", options: ["an in-use VORTAC", "an available VORTAC", "an in-use NDB", "an available NDB"], answer: "an available NDB" },
  { category: "Radio Navigation", test: 4, id: 1014, question: "The middle marker is usually located at a range of ................., with an audio frequency of ................ and illuminates the ................. light.", options: ["4-6 NM 1300 Hz white", "1 km 400 Hz white", "1 km 1300 Hz amber", "1 km 400 Hz amber"], answer: "1 km 1300 Hz amber" },
  { category: "Radio Navigation", test: 4, id: 1015, question: "The main advantage of a continuous wave radar over a pulsed radar is:", options: ["more complex equipment but better resolution and accuracy", "removes the minimum range restriction", "smaller more compact equipment", "permits measurement of Doppler in addition to improved range and bearing"], answer: "removes the minimum range restriction" },
  { category: "Radio Navigation", test: 4, id: 1016, question: "For a conventional VOR a phase difference of 090° would be achieved by flying............... from the beacon:", options: ["west", "north", "east", "south"], answer: "east" },
  { category: "Radio Navigation", test: 4, id: 1017, question: "The ILS glide slope transmitter generates false glide slope signals:", options: ["above the true glide slope", "below the true glide slope", "either side of the localizer", "outside the coverage area"], answer: "above the true glide slope" },
  { category: "Radio Navigation", test: 4, id: 1018, question: "At a range of 200 NM from a VOR, if there is an error of 1°, how far off the centre line is the aircraft?", options: ["3.5 NM", "1.75 NM", "7 NM", "1 NM"], answer: "3.5 NM" },
  { category: "Radio Navigation", test: 4, id: 1019, question: "The coverage of the ILS glide slope with respect to the localizer centre line is:", options: ["±10° to 8 NM", "±10° to 25 NM", "±8° to 10 NM", "±10° to 8 NM"], answer: "±8° to 10 NM" },
  { category: "Radio Navigation", test: 4, id: 1020, question: "Refer to appendix A, diagram F. What is the required track?", options: ["165", "173", "157", "130"], answer: "173" },
  { category: "Radio Navigation", test: 4, id: 1021, question: "The principle of operation of the ILS localizer transmitter is that it transmits two overlapping lobes on:", options: ["different frequencies with different phases", "the same frequency with different phases", "the same frequency with different amplitude modulations", "different frequencies with different amplitude modulations"], answer: "the same frequency with different amplitude modulations" },
  { category: "Radio Navigation", test: 4, id: 1022, question: "The amplitude modulation of the ILS outer marker is ............... and it illuminates the................light in the cockpit.", options: ["400 Hz blue", "1300 Hz amber", "400 Hz amber", "1300 Hz blue"], answer: "400 Hz blue" },
  { category: "Radio Navigation", test: 4, id: 1023, question: "Which of the following systems use pulse technique? 1. secondary surveillance radar 2. airborne weather radar 3. distance measuring equipment 4. primary radar", options: ["all the above", "2 and 4 only", "2 only", "1 and 3 only"], answer: "all the above" },
  { category: "Radio Navigation", test: 4, id: 1024, question: "On an ILS approach you receive more of the 90 Hz modulation than the 150 Hz modulation. The action you should take is:", options: ["fly left and up", "fly left and down", "fly right and up", "fly right and down"], answer: "fly right and up" },
  { category: "Radio Navigation", test: 4, id: 1025, question: "In which frequency band does ILS operate?", options: ["UHF/VHF", "VHF", "SHF", "VLF"], answer: "UHF/VHF" },
  { category: "Radio Navigation", test: 4, id: 1026, question: "Distance on MLS is measured by:", options: ["measuring the time taken for the primary radar pulse to travel from the MLS transmitter to the aircraft receiver", "measuring the time taken for the secondary radar pulse to travel from the MLS transmitter to the aircraft receiver", "phase comparison between the azimuth and elevation beams", "co-located DME"], answer: "co-located DME" },
  { category: "Radio Navigation", test: 4, id: 1027, question: "The coverage of MLS is ............... either side of the centre line to a distance of...............", options: ["40° 40 NM", "40° 20 NM", "20° 20 NM", "20° 40 NM"], answer: "40° 20 NM" },
  { category: "Radio Navigation", test: 4, id: 1028, question: "In which band does the ILS glide path operate?", options: ["metric", "centimetric", "decimetric", "hectometric"], answer: "decimetric" },
  { category: "Radio Navigation", test: 4, id: 1029, question: "Refer to Appendix A, diagram A. What is the deviation from the required track?", options: ["3 NM left", "3 NM right", "8° left", "8° right"], answer: "8° right" },
  { category: "Radio Navigation", test: 4, id: 1030, question: "The definition of a radar display will be best with:", options: ["narrow beamwidth and narrow pulsewidth", "narrow beamwidth and wide pulsewidth", "wide beamwidth and narrow pulsewidth", "wide beamwidth and wide pulsewidth"], answer: "narrow beamwidth and narrow pulsewidth" },
  { category: "Radio Navigation", test: 4, id: 1031, question: "Primary radar operates on the principle of:", options: ["transponder interrogation", "pulse technique", "phase comparison", "continuous wave emission"], answer: "pulse technique" },
  { category: "Radio Navigation", test: 4, id: 1032, question: "The best radar for measuring very short ranges is:", options: ["a continuous wave primary radar", "a pulsed secondary radar", "a pulsed primary radar", "a continuous wave secondary radar"], answer: "a continuous wave primary radar" },
  { category: "Radio Navigation", test: 5, id: 1033, question: "NAVSTAR GPS receiver clock error is removed by:", options: ["regular auto-synchronization with the satellite clocks", "adjusting the pseudo-ranges to determine the error", "synchronization with the satellite clocks on initialization", "having an appropriate atomic time standard within the receiver"], answer: "adjusting the pseudo-ranges to determine the error" },
  { category: "Radio Navigation", test: 5, id: 1034, question: "The main advantage of a slotted scanner is:", options: ["reduces side lobes and directs more energy into the main beam", "removes the need for azimuth slaving", "side lobe suppression", "can produce simultaneous map and weather information"], answer: "reduces side lobes and directs more energy into the main beam" },
  { category: "Radio Navigation", test: 5, id: 1035, question: "The advantages of SSR mode S are:", options: ["improved resolution, TCAS", "data link, reduced voice communications", "TCAS, no RT communications", "better resolution, selective interrogation"], answer: "data link, reduced voice communications" },
  { category: "Radio Navigation", test: 5, id: 1036, question: "The DME IDENT signal consists of a Morse code transmission every:", options: ["30-40 seconds at a pitch of 1350 Hz", "30-40 seconds at a pitch of 1020 Hz", "10-15 seconds at a pitch of 1350 Hz", "10-15 seconds at a pitch of 1020 Hz"], answer: "30-40 seconds at a pitch of 1350 Hz" },
  { category: "Radio Navigation", test: 5, id: 1037, question: "The positioning of a GNSS aerial on an aircraft is:", options: ["in the fin", "on the fuselage as close as possible to the receiver", "on top of the fuselage close to the centre of gravity", "under the fuselage"], answer: "on top of the fuselage close to the centre of gravity" },
  { category: "Radio Navigation", test: 5, id: 1038, question: "The use of the AWR on the ground is:", options: ["not permitted", "permitted provided reduced power is used", "permitted provided special precautions are taken to safeguard personnel and equipment", "only permitted to assist movement in low visibility conditions"], answer: "permitted provided special precautions are taken to safeguard personnel and equipment" },
  { category: "Radio Navigation", test: 5, id: 1039, question: "The airborne weather radar (AWR) cannot detect:", options: ["snow", "moderate rain", "dry hail", "wet hail"], answer: "snow" },
  { category: "Radio Navigation", test: 5, id: 1040, question: "What are the ground components of MLS?", options: ["Separate azimuth and elevation antennae with DME", "Separate azimuth and elevation antennae with middle and outer markers", "Combined azimuth and elevation antennae with DME", "Combined azimuth and elevation antennae with middle and outer markers"], answer: "Separate azimuth and elevation antennae with DME" },
  { category: "Radio Navigation", test: 5, id: 1041, question: "Area navigation is:", options: ["one which enables the aircraft to navigate on any desired flight path within the coverage of appropriate ground based navigation aids or within the specified limits of self-contained on-board systems or a combination of the two", "one which enables the aircraft to navigate on any desired flight path within the coverage of appropriate ground based navigation aids or within the specified limits of self-contained on-board systems but not a combination of the two", "one which enables the aircraft to navigate on any desired flight path within the coverage of appropriate ground based navigation aids only", "one which enables the aircraft to navigate on any desired flight path within the specified limits of self-contained on-board systems"], answer: "one which enables the aircraft to navigate on any desired flight path within the coverage of appropriate ground based navigation aids or within the specified limits of self-contained on-board systems or a combination of the two" },
  { category: "Radio Navigation", test: 5, id: 1042, question: "Which is the most suitable radar for measuring short ranges?", options: ["Millimetric pulse", "Continuous wave primary", "Centimetric pulse", "Continuous wave secondary"], answer: "Continuous wave primary" },
  { category: "Radio Navigation", test: 5, id: 1043, question: "The PRN codes are used to:", options: ["determine the time taken for the signal to reach the receiver", "differentiate between signals from different satellites", "pass navigation information to the receiver", "pass clock correction data to the receiver"], answer: "differentiate between signals from different satellites" },
  { category: "Radio Navigation", test: 5, id: 1044, question: "Refer to Appendix A, diagram D. What is the track deviation?", options: ["0.8 NM left", "0.8 NM right", "8 NM left", "8 NM right"], answer: "0.8 NM left" },
  { category: "Radio Navigation", test: 5, id: 1045, question: "The SSR ground transceiver interrogates on ................ and receives responses on ................", options: ["1030 MHz 1030 MHz", "1030 MHz 1090 MHz", "1090 MHz 1030 MHz", "1090 MHz 1090 MHz"], answer: "1030 MHz 1090 MHz" },
  { category: "Radio Navigation", test: 5, id: 1046, question: "The accuracy of SSR mode C altitude as displayed to the air traffic controller is:", options: ["±25 ft", "±50 ft", "±75 ft", "±100 ft"], answer: "±75 ft" },
  { category: "Radio Navigation", test: 5, id: 1047, question: "Concerning NAVSTAR/GPS orbits, which of the following statements is correct?", options: ["The inclination of the orbits is 55° with an orbital period of 12 hours", "The inclination of the orbits is 55° with an orbital period of 24 hours", "The orbits are geostationary to provide global coverage", "The orbits are inclined at 65° with an orbital period of 11 hours 15 minutes"], answer: "The inclination of the orbits is 55° with an orbital period of 12 hours" },
  { category: "Radio Navigation", test: 5, id: 1048, question: "Why is a secondary radar display free from weather clutter?", options: ["The frequencies are too low to detect water droplets", "The frequencies are too high to detect water droplets", "Moving target indication cancels out weather returns", "Weather returns are filtered out because they do not originate from transponders"], answer: "Weather returns are filtered out because they do not originate from transponders" },
  { category: "Radio Navigation", test: 5, id: 1049, question: "The frequency used for airborne weather radar is:", options: ["9375 MHz", "937.5 MHz", "93.75 GHz", "9375 GHz"], answer: "9375 MHz" },
  { category: "Radio Navigation", test: 5, id: 1050, question: "The vertical position provided by SSR mode C is referenced to:", options: ["QNH unless QFE is in use", "1013.25 hPa", "QNH", "WGS84 datum"], answer: "1013.25 hPa" },
  { category: "Radio Navigation", test: 5, id: 1051, question: "The EHSI is showing 5° fly right with a TO indication. The aircraft heading is 280°(M) and the required track is 270°. The radial is:", options: ["275", "265", "085", "095"], answer: "095" },
  { category: "Radio Navigation", test: 5, id: 1052, question: "The contents of the navigation and systems message from NAVSTAR/GPS SVs include:", options: ["satellite clock error, almanac data, ionospheric propagation information", "satellite clock error, almanac data, satellite position error", "position accuracy verification, satellite clock time and clock error", "ionospheric propagation information, X, Y & Z coordinates and corrections, satellite clock time and error"], answer: "satellite clock error, almanac data, ionospheric propagation information" }
